for pp in positive_phrases:
   scores[pp] = +1.0#/len(pp)
for np in negative_phrases:
   scores[np] = -1.0#/len(np)   
   

def contains(tweet, phrase):
   #print(phrase, scores[phrase])
   return tweet.find(phrase + ' ') != -1 or \
          tweet.find(' ' + phrase) != -1
def positivity(tweet):
   t = ' ' + tweet.lower().replace('.', ' ').replace('  ', ' ') + ' '
   return sum(scores[phrase] for phrase in scores.keys() if contains(t, phrase))


